import React, { Component } from 'react';
import { connect } from 'react-redux';
import { requestCityAPI } from '../redux/actions';

class SelectCity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCity: '',
      city: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({selectedCity: event.target.value});
  }

  async requestCitys() {
    const { country, getCitys, selectedState, city } = this.props;
    await getCitys(country.iso2, selectedState);
    city.map((cityName, index) => {
      return (
      <option
        value={cityName.name}
        key={ index }
      >
        { cityName.name }
      </option>);
    })
  }

  render() {
    const { city, selectedState, country } = this.props;
    console.log(selectedState, country)
    const { selectedCity } = this.state;
    return(
      <select value={ selectedCity } onChange={this.handleChange}>
        {/* { selectedState.length > 0 && country !== undefined ? this.requestCitys()
        : <option>Loading</option> } */}
      </select>
    )
  }
}

const mapStateToProps = (state) => ({
  city: state.city.city,
  // selectedState: state.selectedState.selectedState,
  // country: state.country.country,
});

const mapDispatchToProps = (dispatch) => ({
  getCitys: (countryIso, stateIso) => dispatch(requestCityAPI(countryIso, stateIso)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SelectCity);
