import React, { Component } from 'react';
import { connect } from 'react-redux';

class SelectCity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCity: '',
      selectedState: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({selectedCity: event.target.value});
  }


  render() {
    const { city } = this.props;
    const { selectedCity, selectedState } = this.state;
    console.log(selectedState)
    console.log(city)
    return(
      <select value={ selectedCity } onChange={this.handleChange}>
        { selectedState === 0 ? <option>Loading</option>
        : city.map((cityName, index) => {
          return (
          <option
            value={cityName.name}
            key={ index }
          >
            { cityName.name }
          </option>);
        }) }
      </select>
    )
  }
}

const mapStateToProps = (state) => ({
  city: state.city.city,
  selectedState: state.selectedState.selectedState,
});

export default connect(mapStateToProps)(SelectCity);
