import React, { Component } from 'react';
import { connect } from 'react-redux';

class SelectCity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCity: '',
    };
    this.handleChange = this.handleChange.bind(this);
    this.createCityOptions = this.createCityOptions.bind(this);
  }

  handleChange(event) {
    const { selectedCity } = this.props
    this.setState({selectedCity: event.target.value});
    console.log(selectedCity)
  }

  createCityOptions() {
    const { city } = this.props;
    console.log(city)
    switch (city) {
      case undefined:
        return <option>Loading...</option>;
      default:
        return this.createOptions()
    }
  }

  createOptions() {
    const { city } = this.props;
    return city.map((cityName, index) => {
      return (
      <option
        value={cityName.name}
        key={ index }
      >
        { cityName.name }
      </option>);
    })
  }

  render() {
    const { selectedCity } = this.state;
    return(
      <select value={ selectedCity } onChange={this.handleChange}>
        {this.createCityOptions()}
      </select>
    )
  }
}

const mapStateToProps = (state) => ({
  city: state.city.city,
});

export default connect(mapStateToProps)(SelectCity);
