import React from 'react';
import { connect } from 'react-redux';

const Weather = ({ weather, selectedState }) => {
  return (
    <>
      { [weather].map((wInfo) => {
      return (
      <main key={wInfo.cod}>
        <nav>
          <button type="button">
            New Search
          </button>
        </nav>
        <header>
          <h1>
            {`Country: ${wInfo.sys.country} - State: ${selectedState}`}
          </h1>
          <h1>{`City: ${wInfo.name}`}</h1>
          <h2>Current time: GMT -3 </h2>
        </header>
        <section>
          <p>{`Climate: ${wInfo.weather[0].main} - ${wInfo.weather[0].description} `}</p>
          <p>{`Current Temperature: ${((wInfo.main.temp)- 273.1).toFixed(1)}°C`}</p>
          <p>{`Feels like Temperature: ${((wInfo.main.temp)- 273.1).toFixed(1)}°C`}</p>
          <p>{`Max Temperature: ${((wInfo.main.temp)- 273.1).toFixed(1)}°C`}</p>
          <p>{`Min Temperature: ${((wInfo.main.temp)- 273.1).toFixed(1)}°C`}</p>
        </section>
      </main>)
    })}
    </>
  )
}

const mapStateToProps = (state) => ({
  weather: state.weather.weather,
  selectedState: state.selectedState.stateSelected,
});

export default connect(mapStateToProps)(Weather);
