import React, { Component } from 'react';
import getAllCountries from 'country-state-city';
import { ICountry, IState, ICity } from 'country-state-city';

class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
    country: '',
    };
    this.handleCountry = this.handleCountry.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const {country, value } = event.target;
    this.setState({ [country]: value });
    console.log(value)
  }

  async handleCountry() {
    const {country } = this.state;
    console.log(country)
    var headers = new Headers();
    headers.append("X-CSCAPI-KEY", `MlNERzFKOVQ3VUVid2p6STdWYjY2dFVicmxJRUZpOWhpcVlERmtNQg==
    `);

    var requestOptions = {
      method: 'GET',
      headers: headers,
      redirect: 'follow'
    };

    await fetch(`https://api.countrystatecity.in/v1/countries/$country}`, requestOptions)
      .then(response => response.text())
      .then(result => console.log(result))
      .catch(error => console.log('error', error));
  }

  render() {
    const { country } = this.state;
    return(
      <header>
        <h1>Set your Country, state and city to show your weather in real time!</h1>
        <form>
          <input
            type="text"
          country="country"
            value={ country }
            onChange={ this.handleChange }
          />
          <button
           type="button"
           clascountry="submitWeather"
           onClick={ this.handleCountry }
           >
            Show me Weather
          </button>
          { this.handleCountry }
        </form>
      </header>
    )
  }
}

export default Header;
