import React, { Component } from 'react';
import { connect } from 'react-redux';
import { requestCountryAPI, requestStateAPI } from '../redux/actions';
import SelectState from '../components/SelectState';
import SelectCity from '../components/SelectCity';
import '../style/pages/Login/Login.css'

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: 'BR',
      states: [],
    };
    this.handleCountry = this.handleCountry.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.requestStates = this.requestStates.bind(this);
  }

  componentDidMount() {
    this.handleCountry()
  }

  handleChange(event) {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  async handleCountry() {
    const { getCountry } = this.props;
    const { name } = this.state;
    await getCountry(name);
    await this.requestStates();
  }

  async requestStates() {
    const { country, getStates, states } = this.props;
    await getStates(country.iso2);
    this.setState({states: states});
  }

    render() {
    const { name, states } = this.state;
    const { selectedState, country } = this.props
    return(
      <main className="loginPage">
        <header className="headerContainer">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">

          <path fill="#0099ff" fill-opacity="1" d="M0,256L40,234.7C80,213,160,171,240,144C320,117,400,107,480,133.3C560,160,640,224,720,208C800,192,880,96,960,96C1040,96,1120,192,1200,208C1280,224,1360,160,1400,128L1440,96L1440,0L1400,0C1360,0,1280,0,1200,0C1120,0,1040,0,960,0C880,0,800,0,720,0C640,0,560,0,480,0C400,0,320,0,240,0C160,0,80,0,40,0L0,0Z">
        </path>
        <h1>Welcome to your global weather finder</h1>
        <h3>Set the country, state and city where you want to see the weather in the desired location</h3>

        </svg>

        </header>
        <section className="loginSection">
          <form className="searchContainer">
            <input
              type="text"
              name="name"
              maxLength="3"
              value={ name }
              onChange={ this.handleChange }
              className="inputCountry"
            />
            <button
            type="button"
            className="submitCountry"
            onClick={ this.handleCountry }
            >
              Type your country acronym
            </button>
            { states && <SelectState /> }
            { selectedState.length > 0 && country !== undefined
              ? <SelectCity />
              : console.log("não foi")
            }
          </form>
        </section>
      </main>
    )
  }
}

const mapStateToProps = (state) => ({
  country: state.country.country,
  states: state.states.states,
  city: state.city.city,
  selectedState: state.selectedState.stateSelected,
});

const mapDispatchToProps = (dispatch) => ({
  getCountry: (input) => dispatch(requestCountryAPI(input)),
  getStates: (input) => dispatch(requestStateAPI(input)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Login);
