import React, { Component } from 'react';
import { connect } from 'react-redux';
import { requestCityAPI } from '../redux/actions';

class SelectCity extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedCity: '',
      city: [],
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({selectedCity: event.target.value});
  }

  async requestCitys() {
    const { country, getCitys, states, city } = this.props;
    console.log(country, states)
    // await getCitys(country, selectedState);
    // city.map((cityName, index) => {
    //   return (
    //   <option
    //     value={cityName.name}
    //     key={ index }
    //   >
    //     { cityName.name }
    //   </option>);
    // })
  }

  render() {
    const { city, states, country } = this.props;
    console.log(states, country)
    const { selectedCity } = this.state;
    return(
      <select value={ selectedCity } onChange={this.handleChange}>
        { states !== null && country !== null ? this.requestCitys() : console.log('teste') }
      </select>
    )
  }
}

const mapStateToProps = (state) => ({
  city: state.city.city,
  // selectedState: state.selectedState.selectedState,
  // country: state.country.country,
});

const mapDispatchToProps = (dispatch) => ({
  getCitys: (countryIso, stateIso) => dispatch(requestCityAPI(countryIso, stateIso)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SelectCity);
